RECORD.definition:
=let(
  _record, lambda(self, data, traits,
    lambda(_payload,
      if(
        type(_payload) = 64,
        map(_payload, lambda(key, data("@get")(key))),

        if(
          istext(_payload),
          if(
            _payload = "debug",
            data,
            let(
              all_traits, {"@update"; "@hash"; traits("@keys")},
              idx, xmatch(_payload, all_traits, ),
              if(
                iserror(idx),
                data("@get")(_payload),
                if(
                  idx < 3,
                  choose(
                    idx,
                    lambda(key, value, self(self, data("@update")(key, value), traits)),
                    HASH(HASH(data)&HASH(traits))
                  ),
                  traits("@get")(_payload)(self(self, data, traits))
                )
              )
            )
          ),
          if(
            isnumber(_payload),
            data("@get")(_payload),
            THROW("RECORD received an unexpected payload.")
          )
        )
      )
    )
  ),

  _instantiate, lambda(_instantiate, vector, traits, lambda(_payload,
    if(
      iferror(index(_payload("@kind") = "protocol", 1, 1)),
      let(
        protocol_traits, _payload("@items"),

        trait_constructor, lambda(dict, _instantiate(_instantiate, vector, dict)),

        builder, reduce(
          trait_constructor,
          sort(protocol_traits, sequence(rows(protocol_traits)), ),
          lambda(
            prev,
            cur,
            lambda(
              dict,
              let(
                next, lambda(
                  this,
                  lambda(
                    __payload,
                    if(
                      isna(__payload),
                      prev(dict("@update")(cur, __payload)),
                      this(this)
                    )
                  )
                ),
                next(next)
              )
            )
          )
        ),

        builder(traits)
      ),
      let(
        base_constructor, lambda(list,
          _record(
            _record,
            DICT("@from_list")(tocol(vector), list),
            traits
          )
        ),
        vecrows, rows(tocol(vector)),
        if(
          vecrows < 2,
          base_constructor,
          reduce(
            base_constructor,
            sequence(vecrows - 1),
            lambda(
              prev,
              cur,
              lambda(
                list,
                lambda(value,
                  prev(list("@push")(value))
                )
              )
            )
          )
        )(LIST("@push")(_payload))
      )
    )
  )),

  lambda(vector, _instantiate(
    _instantiate,
    vector,
    DICT
      ("@update")("@kind", lambda(self, kind))
  ))
)
