HASH.definition:
=arrayformula(
  if(
    isna(single(input)),
    let(
      in_repr, input("@repr"),
      if(isna(in_repr), "YCRI94LU", HASH(in_repr)),
    ),
    let(
      numhash, lambda(num, 
        if(
          num = 1, "11111111",
          if(
            num = 0, "00000000",
            let(
              str, to_text(num),
              seq, sequence(len(str)),
              chars, code(mid(str, seq, 1)),
              hash_val, reduce(
                2166136261,
                seq,
                lambda(acc, i,
                  let(
                    xored, bitxor(acc, index(chars, i)),
                    step1, mod(xored * 16777619, 36^8),
                    step2, bitxor(step1, i * 997),
                    step2
                  )
                )
              ),
              mixed, mod(bitxor(hash_val, 12345) * 668265261, 36^8),
              base(mixed, 36, 8)
            )
          )
        )
      ),
      stringhash, lambda(str,
        if(isblank(str), "01Z141Z3",
          if(str="", "P3MQMMIW",
            let(
              seq, sequence(len(str)),
              chars, code(mid(str, seq, 1)),
              hash_val, reduce(
                2166136261,
                seq,
                lambda(acc, i,
                  let(
                    xored, bitxor(acc, index(chars, i)),
                    step1, mod(xored * 16777619, 36^8),
                    step2, bitxor(step1, i * 997),
                    step2
                  )
                )
              ),
              mixed, mod(bitxor(hash_val, 12345) * 668265261, 36^8),
              base(mixed, 36, 8)
            )
          )
        )
      ),
      value, if(isref(input), {input}, input),
      switch(
        type(value),
        1, numhash(value),
        2, stringhash(value),
        4, stringhash("bool"&value),
        16, stringhash("err"&error.type(value)),
        64, stringhash("array"&REPR(value)),
        128, "UE22N1UV",
        THROW("Unsupported type for hash.")
      )
    )
  )
)
